const wcStepperQuantity={init:function(){const allInputInstances=document.querySelectorAll(".wc-stepper-quantity .js-quantity");for(let instanceInput of allInputInstances){this.checkMin(instanceInput);instanceInput.previousElementSibling.addEventListener("click",this.decrease,false);instanceInput.nextElementSibling.addEventListener("click",this.increase,false)}},checkMin:function(instanceInput){let minAttr=instanceInput.getAttribute("min");let inputValue=instanceInput.valueAsNumber;if(inputValue<=minAttr){instanceInput.value=minAttr;instanceInput.previousElementSibling.setAttribute("disabled","")}else{instanceInput.previousElementSibling.removeAttribute("disabled")}},decrease:function(event){const minBtn=event.currentTarget;let inputField=minBtn.nextElementSibling;let currentValue=inputField.valueAsNumber;if(currentValue===1){minBtn.setAttribute("disabled","")}else{inputField.stepDown();wcStepperQuantity.checkMin(inputField);setTimeout(wcStepperQuantity.submitForm,1e3)}},increase:function(event){const plusBtn=event.currentTarget;let inputField=plusBtn.previousElementSibling;inputField.stepUp();wcStepperQuantity.checkMin(inputField);setTimeout(wcStepperQuantity.submitForm,1e3)},submitForm:function(){$("form#cart_form").trigger("submit")}};