const countDownDate=new Date("Oct 16, 2023 00:00:00").getTime();let count=0;let x=setInterval(function(){const now=(new Date).getTime();const distance=countDownDate-now;const days=Math.floor(distance/(1e3*60*60*24));const hours=Math.floor(distance%(1e3*60*60*24)/(1e3*60*60));const minutes=Math.floor(distance%(1e3*60*60)/(1e3*60));const seconds=Math.floor(distance%(1e3*60)/1e3);if(distance>0){document.getElementById("discount-timer").innerHTML="<span class='dash'> - </span>"+days+"d "+hours+"h "+minutes+"m "+seconds+"s "}count++;if(count===1){checkMsgBarHeight(0)}if(distance<0){clearInterval(x);document.getElementById("discount-timer").style.display="none";checkMsgBarHeight(0)}},1e3);function checkMsgBarHeight(scrollPos){const msgBar=document.querySelector("#msg-covid");const msgBarHeight=msgBar.clientHeight;const screenWidth=window.screen.width;const confView=document.querySelector(".configurator__view");if(msgBarHeight>=50&&screenWidth<768&&scrollPos===0){if(confView){confView.style.top="80px"}}else{if(confView){if(scrollPos>=50&&screenWidth<768){confView.style.top="10px"}else{if(confView.style.top){confView.style.removeProperty("top")}if(confView.style.length===0){confView.removeAttribute("style")}}}}}