const rootElement=document.documentElement;const masthead=document.querySelector("#masthead-mobile");const navBtn=document.querySelector("hamburger-button");const backLinks=document.querySelectorAll(".--back");const level1SubviewLinks=document.querySelectorAll(".--nav-level1 > .--has-submenu > .subview-link");const level2SubviewLinks=document.querySelectorAll(".--nav-level2 > .--has-submenu > .subview-link");navBtn.addEventListener("click",()=>{rootElement.classList.toggle("--menu-open");if(masthead.classList.contains("--moveToLevel2")){masthead.classList.remove("--moveToLevel2")}else if(masthead.classList.contains("--moveToLevel3")){masthead.classList.remove("--moveToLevel3")}if(rootElement.classList.contains("--menu-open")){ListItemsAnimateIn()}else{console.log("no way");ListItemsReset()}});level1SubviewLinks.forEach(function(currentElement,currentIndex,listObj){currentElement.addEventListener("click",function(e){handleLevel1Links(currentElement)})});level2SubviewLinks.forEach(function(currentElement,currentIndex,listObj){currentElement.addEventListener("click",function(e){handleLevel2Links(currentElement)})});backLinks.forEach(function(currentElement,currentIndex,listObj){currentElement.addEventListener("click",function(e){handleBackLinks(currentElement)})});function ListItemsAnimateIn(){const MainListItems=document.querySelectorAll(".nav-bar-mobile__link-list.--nav-level1 > li");MainListItems.forEach(function(currentElement,currentIndex,listObj){currentElement.style.opacity=1;currentElement.style.transitionDelay=(currentIndex+1)*.15+"s"});setHeightLevel2()}function ListItemsReset(){const MainListItems=document.querySelectorAll(".nav-bar-mobile__link-list.--nav-level1 > li");MainListItems.forEach(function(currentElement,currentIndex,listObj){currentElement.removeAttribute("style")})}function handleLevel1Links(currentElement){event.preventDefault();masthead.querySelectorAll(".--active").forEach(function(currentElement,currentIndex,listObj){currentElement.classList.remove("--active")});currentElement.classList.add("--active");masthead.classList.add("--moveToLevel2")}function handleLevel2Links(currentElement){event.preventDefault();currentElement.classList.add("--active");masthead.classList.remove("--moveToLevel2");masthead.classList.add("--moveToLevel3")}function handleBackLinks(currentElement){event.preventDefault();if(masthead.classList.contains("--moveToLevel2")){masthead.classList.remove("--moveToLevel2")}else if(masthead.classList.contains("--moveToLevel3")){masthead.classList.remove("--moveToLevel3");masthead.classList.add("--moveToLevel2")}currentElement.parentElement.parentElement.parentElement.querySelector(".--active").classList.remove("--active")}function setHeightLevel2(){const Level2Elements=Array.from(document.querySelectorAll(".submenu.--nav-level2"));const maxValueInArray=Level2Elements.reduce((maxElement,currentElement)=>{const maxHeight=Math.max(maxElement.offsetHeight,currentElement.offsetHeight);return maxHeight===maxElement.offsetHeight?maxElement:currentElement},Level2Elements[0]);console.log("maxValueInArray:"+maxValueInArray);Level2Elements.forEach(element=>{element.style.height=maxValueInArray.offsetHeight+"px"})}