const countDownDate=new Date("Oct 15, 2023 23:59:59").getTime();let count=0;let x=setInterval(function(){const now=(new Date).getTime();let distance=countDownDate-now;const days=Math.floor(distance/(1e3*60*60*24));const hours=Math.floor(distance%(1e3*60*60*24)/(1e3*60*60));const minutes=Math.floor(distance%(1e3*60*60)/(1e3*60));const seconds=Math.floor(distance%(1e3*60)/1e3);if(distance>0){document.getElementById("discount-timer").innerHTML="<span class='dash'> - </span>"+days+"d "+hours+"h "+minutes+"m "+seconds+"s "}count++;if(distance<0){clearInterval(x);document.getElementById("discount-timer").style.display="none";document.getElementById("msg-covid").classList.remove("sh-org-msg-bar--discount")}},1e3);